import React from 'react';
import './CreateJob.css';
import axios from 'axios';
import {  Button,
          Col,
          Form,
          FormGroup,
          FormControl,
          FormLabel } from 'react-bootstrap';

class CreateJob extends React.Component {
  state = {

    jobData: {},
  }

  // Updates BUILDER
  handleBuilder = event => {
    let value = event.target.value;
    this.setState({
      jobData: {
        ...this.state.jobData,
        client: value,
        address: value,
      }
    });
  }

  // Updates ADDRESS
  handleAddress = event => {
    let value = event.target.value;
    this.setState({
      userData: {
        ...this.state.jobData,
        address: value
      }
    });
  }


  handleSubmit = event => {
    // const PORT = process.env.PORTDB;
    event.preventDefault();
    console.log('Creating JOB...');
    console.log('bodytosend', this.state.jobData);
    // document.getElementById('location').disabled = "null";
    document.getElementById('client').disabled = "null";
    document.getElementById('address').disabled = "null";
    // document.getElementById('picture').disabled = "null";
    axios.post(`http://localhost:4000/api/v1/jobs/create`, this.state.userData )
      .then(res => {
        console.log('Inserting JOB into DB...')
        console.log(res)
        console.log(res.data)
        this.props.handleClose();
      })
      .catch(err => {
        console.log(err.response)
        console.log('JOB CREATE Fail...')
      });
  };
    render() {
    console.log('Rendering ADD NEW JOB');
    return (
      <>
      <div>
        <Form>
          {/* JOB NAME entry */}
          <FormGroup>
            <FormLabel>Builder</FormLabel>
            <FormControl id="client" type="text" placeholder="Builder" onChange={this.handleBuilder}/>
          </FormGroup>

          {/* CLIENT entry */}
          <FormGroup>
            <FormLabel>Builder</FormLabel>
            <FormControl id="client" type="text" placeholder="Builder" onChange={this.handleBuilder}/>
          </FormGroup>

          {/* LOCATION */}
          <FormGroup>
            <FormLabel>Location</FormLabel>
            <FormControl id="location" type="text" placeholder="Locations" onChange={this.handleLocation}/>
          </FormGroup>

          {/* ADDRESS */}
          <FormGroup>
            <FormLabel>Address</FormLabel>
            <FormControl id="address" type="text" placeholder="Address" onChange={this.handleAddress}/>
          </FormGroup>

          {/* CITY */}
          <FormGroup>
            <FormLabel>City</FormLabel>
            <FormControl id="city" type="text" placeholder="City" onChange={this.handleCity}/>
          </FormGroup>

          {/* CITY */}
          <FormGroup>
            <FormLabel>City</FormLabel>
            <FormControl id="city" type="text" placeholder="City" onChange={this.handleCity}/>
          </FormGroup>

          {/* CITY */}
          <FormGroup>
            <FormLabel>City</FormLabel>
            <FormControl id="city" type="text" placeholder="City" onChange={this.handleCity}/>
          </FormGroup>

          {/* CITY */}
          <FormGroup>
            <FormLabel>City</FormLabel>
            <FormControl id="city" type="text" placeholder="City" onChange={this.handleCity}/>
          </FormGroup>

         {/* SUBMIT button */}
          <FormGroup>
            <Col smOffset={2} sm={10}>
              <Button value='Submit' type='submit' onClick={this.handleSubmit} >Add New Job</Button>
            </Col>
          </FormGroup>

        </Form>
      </div>
      </>
    );
  }
}
export default CreateJob;
