import React from 'react';
import axios from 'axios';
import './JobList.css';
// import { Table } from 'react-bootstrap';
import Job from '../Job';

class JobList extends React.Component {

  state = {
    id: "",
    jobName: "",
    client: "",
    location: "",
    address: "",
    city: "",
    type: "",
    siteContact: "",
    status: "",
    price: "",
    paid: false,

  }

  componentDidMount = () => {
    const PORT = process.env.PORT;
    console.log(" JOB LIST")
    axios.get(`http://localhost:${PORT}/api/v1/jobs`)
      .then(res=> {
        console.log('data',res.data);
        this.setState({
          content:res.data
        })

      })
      .catch( err =>
        console.log(err)
      )
    }

    // expand = () => {

    // }

    render() {
      console.log(this.state.jobName);
      let jobs=null;
      jobs = this.state.jobName.map(job => {
        console.log(job._id)
        return <Job key={job._id} post={job}/>
      })
      console.log(jobs)
      return(
        <div className="postContainer">
          {jobs}
        </div>

      )
    }
  }


