import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Navbar, Modal } from 'react-bootstrap';
import Dashboard from './components/Dashboard/Dashboard'
import CreateJob from './components/Job/CreateJob/CreateJob'
import JobList from './components/Job/JobList/JobList'

import './App.css';



class App extends React.Component {
  constructor(props, context) {
    super(props, context);
    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
    // this.handleSignupShow = this.handleSignupShow.bind(this);
    // this.handleSignupClose = this.handleSignupClose.bind(this);
    this.state = {
      createpostshow: false,
      // signupshow:false,
      currentCityPost:[],
      cities:[]
    };
  }

//GET, all city , move from cityList
componentDidMount(){
  const PORT = process.env.PORT;
  axios.get(`http://localhost:${PORT}/api/v1/location`)
    .then(res => {
      console.log(res.data)
      res.data.AllLocation.sort((a,b) => a.cityId - b.cityId)
      this.setState({
        cities: res.data.AllLocation
      })
      let sfId = res.data.AllLocation.filter(location => location.cityId == 1)[0]._id
      this.handleCityClick(sfId);
    })
    .catch(err => {
      console.log(err.response)
    })

}

// Handle OPEN and CLOSE of CREATE POST
handleShow() {
  this.setState({ createpostshow: true });
}

handleClose() {
  this.setState({ createpostshow: false });
}




  render() {
    return (
      <>
        <Navbar bg="dark" variant="dark">
          <Navbar.Brand href="#home">
            <img
              alt=""
              src="/logo.svg"
              width="30"
              height="30"
              className="d-inline-block align-top"
            />{' '}
            Builders Waste Recycling
          </Navbar.Brand>
        </Navbar>

        {/* <h4>Job Dashboard:</h4> */}
        <button id="createjobbtn" type="create-job-button" className="btn btn-success mt-1 btn-block" onClick={this.handleCreate}>New Job</button>

        {/* <button id="pickupreturnbtn" type="pickup-return-button" className="btn btn-primary mt-1 btn-block" onClick={this.handlePickupReturn}>Pickup Return</button>

        <button id="returnbtn" type="return-button" className="btn btn-danger mt-1 btn-block" onClick={this.handleReturn}>Return Only</button> */}

        <Dashboard />

        {/* <br/> */}
        {/* <h4>Trailers in the field:</h4> */}
        <JobList />
        <Modal show={this.state.createjobshow} onHide={this.handleClose}>
            <Modal.Header closeButton>
            <h2>Create Post</h2>
            </Modal.Header>
            <Modal.Body>
              <CreateJob setCurrentUser={this.props.setCurrentUser} handleClose={this.handleClose}/>
              <hr />
            </Modal.Body>
          </Modal>


      </>
    );
  }
}

export default App;
