import React from 'react';
import './Dashboard.css';
import axios from 'axios';
import Table from 'react-bootstrap/Table';


class Dashboard extends React.Component {

  state = {
    id: "",
    jobName: [],
    trailer: [],
    status: "",
  }

  componentDidMount = () => {
    // const PORTDB = process.env.PORTDB;
    console.log("Getting number of JOBS...")
    axios.get(`http://localhost:4000/api/v1/jobs`)
      .then(res=> {
        console.log('data',res.data);
        this.setState({
          jobName:res.data
        })

      })
      .catch( err =>
        console.log(err)
      )
  }

  trailerCount = () => {
    let littleT = 0;
    let bigT = 0;
    for (i=0; i<this.state.jobName.length; i++)
      if(this.state.trailer === 10){
        littleT++;
      } else if(this.state.trailer === 15){
        bigT++;
      }


    console.log(this.state.jobName.length);
  }

  render () {
    console.log('There are this many jobs...');
    this.trailerCount()
    return (
      <>
        <div>
          <Table className="col-sm-8" striped border hover responsive="sm" variant="dark">
            <thead>
              <tr>
                <th>Trailer</th>
                <th>Total</th>
                <th>Out</th>
                <th>Available</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>10 yd</td>
                <td>150</td>
                <td>{this.state.jobName.length}</td>
                <td>10</td>
              </tr>
              <tr>
                <td>15 yd</td>
                <td>60</td>
                <td>55</td>
                <td>5</td>
              </tr>
              <tr>
                <td>Total</td>
                <td>210</td>
                <td>195</td>
                <td>15</td>
              </tr>
            </tbody>
          </Table>
        </div>
      </>
    )  
  }
}

export default Dashboard;
