import React from 'react';
import axios from 'axios';
import './JobList.css';
import { Table } from 'react-bootstrap';
import Job from './Job/Job';

class JobList extends React.Component {

  state = {
    id: "",
    jobName: [],
    client: "",
    location: "",
    address: "",
    city: "",
    type: "",
    siteContact: "",
    status: "",
    price: "",
    paid: false,
    jobData: this.props.jobData
  }

  // componentDidMount = () => {

  //   // const PORTDB = process.env.PORTDB;
