import React from 'react';
import axios from 'axios';
import {  Modal,
          Button,
          Col,
          Form,
          FormGroup,
          FormControl,
          FormLabel
        } from 'react-bootstrap';
import './Job.css';
// import JobDetail from './JobDetail/JobDetail'

class Job extends React.Component {
  constructor(props, context) {
    super(props, context);
    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);

    this.state = {
      jobdetailshow: false,
      jobData: this.props.job,
      editing: false

    };
  }



  handleShow() {
    this.setState({ jobdetailshow: true });
  }

  handleClose() {
    this.setState({ jobdetailshow: false });
  }

  handlePickupReturn = () => {
    console.log('Pickup Return...')
    axios.put(`http://localhost:4000/api/v1/jobs/${this.props.job._id}`)
      .then(res => {
        console.log(res)
        this.handleClose();
      })
      .catch(err => {
        console.log('PICKUP RETURN failed...')
        console.log(err.response)
      })
  }

  handlePickupOnly = () => {
    console.log('Pickup Only...');
    axios.delete(`http://localhost:4000/api/v1/jobs/${this.props.job._id}`)
      .then(res => {
        console.log(res)
        this.handleClose();
        })
      .catch(err => {
        console.log('DELETE failed...')
        console.log(err.response)
      })
  }

  showJobDetail = () => {
    console.log('Show JOB...')
    console.log(this.props.job._id)
  }

  handleEditJob = (event) => {
    event.preventDefault();
    this.setState({ editing: true })
    console.log('Edit?', this.state.editing)
  }

  render() {
    // console.log(this.state.jobData);
    return (
      <>
        {/* <tr onClick={this.showJobDetail}> */}
        <tr onClick={this.handleShow}>
          <td>{this.props.job.client}</td>
          <td>{this.props.job.address}</td>
          <td>{this.props.job.city}</td>
          <td>{this.props.job.trailer}</td>
        </tr>
        <Modal show={this.state.jobdetailshow} onHide={this.handleClose} variant="dark">
          <Modal.Header closeButton>
            <div class="row">
              <div class="col">
                <h2>Job Detail</h2>
              </div>
              <div class="col">
                <button id="editJob" type="edit-job-button" className="btn btn-primary mt-1 btn-block" onClick={this.handleEditJob}>Edit</button>
              </div>
            </div>
          </Modal.Header>
          <Modal.Body>
            <div>
              <p>{this.props.job.client}</p>
              <p>{this.props.job.location}</p>
              <p>{this.props.job.address}</p>
              <p>{this.props.job.city}</p>
              <hr />
              <p>Job Type: {this.props.job.type}</p>
              <p>Contact: {this.props.job.siteContact}</p>
              <p>{this.props.job.trailer} yd trailer</p>
              <hr />
              <p>Price: ${this.props.job.price}</p>
              <p>Paid: {this.props.job.paid}</p>
              <p>Dropped: {new Date(this.props.job.createdAt).toUTCString().slice(5, 16)}</p>
            </div>
          {this.state.editting && 
            <div>
              <Form>
                {/* JOB NAME entry */}
                <FormGroup>
                  {/* <FormLabel>Builder</FormLabel> */}
                  <FormControl name="jobName" type="text" value={this.props.job.jobName} placeholder="Job Name" onChange={this.handleChange}/>
                </FormGroup>

                {/* CLIENT entry */}
                <FormGroup>
                  {/* <FormLabel>Builder</FormLabel> */}
                  <FormControl name="client" type="text" value={this.props.job.client} placeholder="Builder" onChange={this.handleChange}/>
                </FormGroup>

                {/* LOCATION */}
                <FormGroup>
                  {/* <FormLabel>Location</FormLabel> */}
                  <FormControl name="location" type="text" value={this.props.job.location} placeholder="Location" onChange={this.handleChange}/>
                </FormGroup>

                {/* ADDRESS */}
                <FormGroup>
                  {/* <FormLabel>Address</FormLabel> */}
                  <FormControl name="address" type="text" value={this.props.job.address} placeholder="Address" onChange={this.handleChange}/>
                </FormGroup>

                {/* CITY */}
                <FormGroup>
                  {/* <FormLabel>City</FormLabel> */}
                  <FormControl name="city" type="text" value={this.props.job.city} placeholder="City" onChange={this.handleChange}/>
                </FormGroup>

                {/* TYPE of job */}
                <FormGroup>
                  {/* <FormLabel>Type of Job</FormLabel> */}
                  <FormControl name="type" type="text" value={this.props.job.type} placeholder="Type of Job" onChange={this.handleChange}/>
                </FormGroup>

                {/* SITE CONTACT */}
                <FormGroup>
                  {/* <FormLabel>Site Contact</FormLabel> */}
                  <FormControl name="siteContact" type="text" value={this.props.job.siteContact} placeholder="Site Contact" onChange={this.handleChange}/>
                </FormGroup>

                {/* TRAILER */}
                <FormGroup>
                  {/* <FormLabel>City</FormLabel> */}
                  <FormControl name="trailer" type="text" value={this.props.job.trailer} placeholder="Trailer" onChange={this.handleChange}/>
                </FormGroup>

                {/* PRICE */}
                <FormGroup>
                  {/* <FormLabel>Price</FormLabel> */}
                  <FormControl name="price" type="number" value={this.props.job.price} placeholder="Price" onChange={this.handleChange}/>
                </FormGroup>

              {/* SUBMIT button */}
                <FormGroup>
                  <Col sm={10}>
                    <Button value='Submit' type='submit' onClick={this.handleSubmit} >Add New Job</Button>
                  </Col>
                </FormGroup>
                <FormGroup>
                <Col smOffset={2} sm={10}>
                  <Button value='Submit' type='submit' onClick={this.handleEditSubmit} >Submit</Button>
                </Col>
                </FormGroup>
              </Form>
            </div>
          }
          </Modal.Body>
        </Modal>
      </>
    )
  }
}

export default Job

