            <FormControl id="img" type="text" placeholder="photo url" onChange={this.handleImg}/>
          </FormGroup>

         {/* SUBMIT button */}
          <FormGroup>
            <Col smOffset={2} sm={10}>
              <Button value='Submit' type='submit' onClick={this.handleSubmit} >Submit</Button>
            </Col>
          </FormGroup>

        </Form>
      </div>
      </>
    );
  }
}
