import React from 'react';
import './Dashboard.css';
import axios from 'axios';
import Table from 'react-bootstrap/Table';


class Dashboard extends React.Component {

  state = {
    id: "",
    jobName: [],
    info: [],
    status: "",
    littleT: 0,
    bigT: 0
  }

  componentDidMount = () => {
    // const PORTDB = process.env.PORTDB;
    console.log("Getting number of JOBS...")
    axios.get(`http://localhost:4000/api/v1/jobs`)
      .then(res=> {
        console.log('data',res.data);
        this.setState({
          info:res.data
        }, () => {
          this.trailerCount()
        })

      })
      .catch( err =>
        console.log(err)
      )
  }

  trailerCount = () => {
    let data = this.state.info
    let littleT = 0;
    let bigT = 0;
    console.log(data.length);
    console.log('Look here', data[1].trailer);
    for (let i=0; i<data.length; i++){
      if(data[i].trailer === 10){
        littleT++;
        console.log('Adding Little')
      } else if(data[i].trailer === 15){
        bigT++;
        console.log('Adding Big');
      }
      if(i === data.length-1){
        this.setState({
          bigT, littleT
        })
      }
    } 


      // return (
      //   console.log('10yr Trailers:', littleT)
        // console.log('15yd Trailers:', bigT)
      // )
  }

  render () {
    console.log('There are this many jobs...');

    return (
      <>
        <div>
          <Table className="col-sm-8" striped border hover responsive="sm" variant="dark">
            <thead>
              <tr>
                <th>Trailer</th>
                <th>Total</th>
                <th>Out</th>
                <th>Available</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>10 yd</td>
                <td>150</td>
                <td>{this.state.littleT}</td>
                <td>10</td>
              </tr>
              <tr>
                <td>15 yd</td>
                <td>60</td>
                <td>{this.state.bigT}</td>
                <td>5</td>
              </tr>
              <tr>
                <td>Total</td>
                <td>210</td>
                <td>{this.state.littleT}+</td>
                <td>15</td>
              </tr>
            </tbody>
          </Table>
        </div>
      </>
    )  
  }
}

export default Dashboard;
